package com.cy.pj.entity;

public class tb_house_price {

    //房屋id	
	private Integer houseId;
	//基础价
	private Double basePrice;
	//工作日基础价
	private Double weekdayPrice;
	//周末基础价
	private Double weekendPrice;
	//0:周末同价1.周末不同价
	private Integer chargingMode;
	//最高价
	private Double maxPrice;
	//最低价
	private Double minPrice;
	//最少预定天数
	private Integer minDay;

	public tb_house_price() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Integer getHouseId() {
		return houseId;
	}

	public void setHouseId(Integer houseId) {
		this.houseId = houseId;
	}

	public Double getBasePrice() {
		return basePrice;
	}

	public void setBasePrice(Double basePrice) {
		this.basePrice = basePrice;
	}

	public Double getWeekdayPrice() {
		return weekdayPrice;
	}

	public void setWeekdayPrice(Double weekdayPrice) {
		this.weekdayPrice = weekdayPrice;
	}

	public Double getWeekendPrice() {
		return weekendPrice;
	}

	public void setWeekendPrice(Double weekendPrice) {
		this.weekendPrice = weekendPrice;
	}

	public Integer getChargingMode() {
		return chargingMode;
	}

	public void setChargingMode(Integer chargingMode) {
		this.chargingMode = chargingMode;
	}

	public Double getMaxPrice() {
		return maxPrice;
	}

	public void setMaxPrice(Double maxPrice) {
		this.maxPrice = maxPrice;
	}

	public Double getMinPrice() {
		return minPrice;
	}

	public void setMinPrice(Double minPrice) {
		this.minPrice = minPrice;
	}

	public Integer getMinDay() {
		return minDay;
	}

	public void setMinDay(Integer minDay) {
		this.minDay = minDay;
	}

	public tb_house_price(Integer houseId, Double basePrice, Double weekdayPrice, Double weekendPrice,
			Integer chargingMode, Double maxPrice, Double minPrice, Integer minDay) {
		super();
		this.houseId = houseId;
		this.basePrice = basePrice;
		this.weekdayPrice = weekdayPrice;
		this.weekendPrice = weekendPrice;
		this.chargingMode = chargingMode;
		this.maxPrice = maxPrice;
		this.minPrice = minPrice;
		this.minDay = minDay;
	}

	@Override
	public String toString() {
		return "tb_house_price [houseId=" + houseId + ", basePrice=" + basePrice + ", weekdayPrice=" + weekdayPrice
				+ ", weekendPrice=" + weekendPrice + ", chargingMode=" + chargingMode + ", maxPrice=" + maxPrice
				+ ", minPrice=" + minPrice + ", minDay=" + minDay + "]";
	}

	
	
}
